
CREATE TABLE IF NOT EXISTS `carrinho` (
  `id_carrinho` int(11) NOT NULL AUTO_INCREMENT,
  `id_pedido` int(11) NOT NULL,
  `id_produto` int(11) NOT NULL,
  `valor` decimal(10,2) NOT NULL,
  `qtde` int(11) NOT NULL,
  PRIMARY KEY (`id_carrinho`)
) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=11 ;

-- --------------------------------------------------------


CREATE TABLE IF NOT EXISTS `categoria` (
  `id_categoria` int(11) NOT NULL AUTO_INCREMENT,
  `categoria` varchar(100) NOT NULL,
  `ativo_categoria` varchar(1) DEFAULT NULL,
  `slug_categoria` varchar(50) NOT NULL,
  `icone` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`id_categoria`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=8 ;




-- --------------------------------------------------------



CREATE TABLE IF NOT EXISTS `cliente` (
  `id_cliente` int(11) NOT NULL AUTO_INCREMENT,
  `tipo` char(1) DEFAULT NULL,
  `empresa` varchar(255) NOT NULL,
  `contato` varchar(255) DEFAULT NULL,
  `cnpj` varchar(50) DEFAULT NULL,
  `IncEst` varchar(100) DEFAULT NULL,
  `cliente` varchar(200) NOT NULL,
  `cpf` varchar(50) DEFAULT NULL,
  `RG` varchar(50) DEFAULT NULL,
  `endereco` varchar(200) DEFAULT NULL,
  `numero` varchar(10) DEFAULT NULL,
  `complemento` varchar(60) DEFAULT NULL,
  `bairro` varchar(80) DEFAULT NULL,
  `cidade` varchar(60) NOT NULL,
  `uf` varchar(50) NOT NULL,
  `cep` varchar(20) NOT NULL,
  `ddd` char(5) DEFAULT NULL,
  `fone1` varchar(20) DEFAULT NULL,
  `fone2` varchar(20) DEFAULT NULL,
  `sexo` varchar(1) DEFAULT NULL,
  `email` varchar(200) DEFAULT NULL,
  `senha` varchar(60) DEFAULT NULL,
  `data_cadastro` date DEFAULT NULL,
  `ativo_cliente` varchar(1) DEFAULT NULL,
  PRIMARY KEY (`id_cliente`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=3 ;


-- --------------------------------------------------------



CREATE TABLE IF NOT EXISTS `fabricante` (
  `id_fabricante` int(11) NOT NULL AUTO_INCREMENT,
  `fabricante` varchar(150) NOT NULL,
  `ativo_fabricante` varchar(1) NOT NULL,
  PRIMARY KEY (`id_fabricante`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=25 ;






CREATE TABLE IF NOT EXISTS `itens` (
  `id_itens` int(11) NOT NULL AUTO_INCREMENT,
  `id_venda` int(11) DEFAULT NULL,
  `id_produto` int(11) DEFAULT NULL,
  `valor_item` decimal(10,2) NOT NULL,
  `qtde_item` int(11) NOT NULL,
  `subtotal` decimal(10,2) NOT NULL,
  PRIMARY KEY (`id_itens`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=6 ;





CREATE TABLE IF NOT EXISTS `pedido` (
  `id_pedido` int(11) NOT NULL AUTO_INCREMENT,
  `id_cliente` int(11) DEFAULT NULL,
  `qtd` int(11) DEFAULT NULL,
  `data_pedido` date NOT NULL,
  PRIMARY KEY (`id_pedido`)
) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=6 ;






CREATE TABLE IF NOT EXISTS `produto` (
  `id_produto` int(11) NOT NULL AUTO_INCREMENT,
  `id_categoria` int(11) NOT NULL,
  `id_subcategoria` int(11) NOT NULL,
  `id_fabricante` int(11) DEFAULT NULL,
  `produto` varchar(200) NOT NULL,
  `preco_alto` decimal(10,2) NOT NULL,
  `preco` decimal(10,2) NOT NULL,
  `descricao` text NOT NULL,
  `detalhes` text NOT NULL,
  `ativo_produto` varchar(1) NOT NULL,
  `imagem` varchar(150) DEFAULT NULL,
  `destaque` varchar(1) NOT NULL,
  PRIMARY KEY (`id_produto`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=45 ;






CREATE TABLE IF NOT EXISTS `slideshow` (
  `id_slideshow` int(11) NOT NULL AUTO_INCREMENT,
  `titulo` varchar(150) NOT NULL,
  `imagem` varchar(150) NOT NULL,
  `url` varchar(200) NOT NULL,
  `ativo` varchar(1) DEFAULT NULL,
  PRIMARY KEY (`id_slideshow`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=4 ;






CREATE TABLE IF NOT EXISTS `subcategoria` (
  `id_subcategoria` int(11) NOT NULL AUTO_INCREMENT,
  `id_categoria` int(11) NOT NULL,
  `subcategoria` varchar(100) NOT NULL,
  `ativo_subcategoria` varchar(1) NOT NULL,
  PRIMARY KEY (`id_subcategoria`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=64 ;






CREATE TABLE IF NOT EXISTS `venda` (
  `id_venda` int(11) NOT NULL AUTO_INCREMENT,
  `id_cliente` int(11) DEFAULT NULL,
  `data_venda` date DEFAULT NULL,
  `pago` varchar(1) DEFAULT NULL,
  `forma_pagamento` varchar(200) DEFAULT NULL,
  PRIMARY KEY (`id_venda`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=4 ;



